mounted: function(){
		let SELF = this;

		let tiny_instance= class extends mats.system_module_tinymce_utilization_formatted_text {
			config() {
				let config = super.config();
				config.paste_data_images = true;
				config.automatic_uploads = true;
				config.images_upload_url = SELF.file_upload_url;
				return config;
			}
		}

		let params = {
			settings: {
				show_text_formatting: true,
				show_text_positioning: true,
				show_links: true,
				show_tables: true,
				show_images: true,
				show_editing_aids: true
			}
		}
		new tiny_instance($('#editor_container'), params);
}

